mapper.select:
select 

DISTINCT 


FROM  shop WHERE 1=1 

AND shop_name LIKE CONCAT(#{shopsdto.shopName},'%')


AND license_no = #{shopsdto.licenseNo}


AND province_id = #{shopsdto.provinceId} 


AND city_id = #{shopsdto.cityId}


AND pf_user_id = #{shopsdto.pfUserId}


AND status = #{shopsdto.status}


LIMIT #{shopsdto.pageNum},#{shopsdto.pageSize}
